import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [usernameError, setUsernameError] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleUsernameChange = (e) => {
        const value = e.target.value;
        setUsername(value);
        setUsernameError("");
        if (value.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
        }
    };

    const handleSignup = async (e) => {
        e.preventDefault();

        if (username.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
            return;
        }

        setIsLoading(true);

        try {
            const response = await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            // Handle successful signup (clear inputs, navigate to login, etc.)
            setUsername("");
            setPassword("");
            setIsLoading(false);
            navigate("/login");
        } catch (err) {
            setIsLoading(false);
            if (err.response && err.response.data) {
                setError(err.response.data.error); // For any other error like username already exists
            } else {
                setError("An error occurred. Please try again.");
            }
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Signup</h2>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    {/* Username */}
                    <div>
                        <label htmlFor="username" className="block">Username</label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={handleUsernameChange}
                            className={`w-full p-2 border rounded ${usernameError ? "border-red-500" : "border-gray-300"}`}
                            required
                        />
                        {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    </div>

                    {/* Password */}
                    <div>
                        <label htmlFor="password" className="block">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
                        disabled={isLoading}
                    >
                        {isLoading ? "Signing Up..." : "Signup"}
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Already have an account?{" "}
                    <button onClick={() => navigate("/login")} className="text-blue-500 underline">
                        Login
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
