import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [usernameError, setUsernameError] = useState("");
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        // Validate username length
        if (username.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
            return;
        } else {
            setUsernameError("");  // Clear username error if valid
        }

        try {
            // Assuming the backend has an endpoint to check if the username is unique
            const userCheckResponse = await axios.post("http://localhost:5000/api/auth/check-username", { username });

            if (!userCheckResponse.data.isUnique) {
                setError("Username is already taken.");
                return;
            }

            // Proceed with login if validation passes
            const response = await axios.post("http://localhost:5000/api/auth/login", { username, password });
            localStorage.setItem("token", response.data.token);
            navigate("/home");
        } catch (err) {
            setError(err.response.data.error || "Login failed.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <form onSubmit={handleLogin} className="space-y-4">
                    <div className="flex items-center space-x-2">
                        <label htmlFor="username" className="text-sm font-medium text-gray-700">Username</label>
                        <input
                            type="text"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                    {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    <div className="flex items-center space-x-2">
                        <label htmlFor="password" className="text-sm font-medium text-gray-700">Password</label>
                        <input
                            type="password"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Login
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Don’t have an account?{" "}
                    <button onClick={() => navigate("/")} className="text-blue-500 underline">
                        Signup
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Login;
