import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [usernameError, setUsernameError] = useState("");

    const navigate = useNavigate();

    // Handle username input change and validate
    const handleUsernameChange = async (e) => {
        const value = e.target.value;
        setUsername(value);

        // Check if username is at least 8 characters long
        if (value.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
        } else {
            setUsernameError("");
            try {
                const response = await axios.post("http://localhost:5000/api/auth/check-username", { username: value });
                if (response.status === 200) {
                    setUsernameError(""); // Username is available
                }
            } catch (err) {
                setUsernameError("Username is already taken.");
            }
        }
    };

    // Handle form submission for signup
    const handleSignup = async (e) => {
        e.preventDefault();

        // Validate password and confirm password
        if (password !== confirmPassword) {
            setError("Passwords do not match.");
            return;
        }

        try {
            // Send signup request to the backend
            const response = await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            if (response.status === 201) {
                alert("Signup successful!");
                setTimeout(() => navigate("/login"), 2000); // Redirect to login page
            }
        } catch (err) {
            setError(err.response?.data?.error || "Signup failed.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <div className="flex flex-col">
                        <label htmlFor="username" className="text-sm font-medium text-gray-700">Username</label>
                        <input
                            type="text"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={handleUsernameChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    </div>

                    <div className="flex flex-col">
                        <label htmlFor="password" className="text-sm font-medium text-gray-700">Password</label>
                        <input
                            type="password"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>

                    <div className="flex flex-col">
                        <label htmlFor="confirmPassword" className="text-sm font-medium text-gray-700">Confirm Password</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            placeholder="Confirm Password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>

                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Sign Up
                    </button>
                </form>

                <p className="mt-4 text-center">
                    Already have an account? <button onClick={() => navigate("/login")} className="text-blue-500 underline">Login</button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
