import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash } from "react-icons/fa";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [passwordCriteria, setPasswordCriteria] = useState({
        lowercase: false,
        uppercase: false,
        specialChar: false,
    });
    const [usernameError, setUsernameError] = useState("");

    const navigate = useNavigate();

    const togglePasswordVisibility = () => {
        setPasswordVisible(!passwordVisible);
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        setPassword(value);

        setPasswordCriteria({
            lowercase: /[a-z]/.test(value),
            uppercase: /[A-Z]/.test(value),
            specialChar: /[!@#$%^&*(),.?":{}|<>]/.test(value),
        });
    };

    const handleUsernameChange = async (e) => {
        const value = e.target.value;
        setUsername(value);

        if (value.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
        } else {
            setUsernameError("");
            try {
                const response = await axios.post("http://localhost:5000/api/auth/check-username", { username: value });
                if (!response.data.isUnique) {
                    setUsernameError("Username is already taken.");
                } else {
                    setUsernameError("");
                }
            } catch (err) {
                setUsernameError("");
            }
        }
    };

    const handleSignup = async (e) => {
        e.preventDefault();

        if (!passwordCriteria.lowercase || !passwordCriteria.uppercase || !passwordCriteria.specialChar) {
            setError("Password does not meet the requirements.");
            return;
        }

        if (password !== confirmPassword) {
            setError("Passwords do not match.");
            return;
        }

        if (username.length < 8) {
            setError("Username must be at least 8 characters.");
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            alert("Signup successful!");
            setTimeout(() => navigate("/login"), 2000);
        } catch (err) {
            setError(err.response?.data?.error || "Signup failed.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {error && <p className="text-red-500 text-sm text-center">{error}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <div className="flex flex-col">
                        <label htmlFor="username" className="text-sm font-medium text-gray-700">Username</label>
                        <input
                            type="text"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={handleUsernameChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    </div>

                    <div className="relative flex flex-col">
                        <label htmlFor="password" className="text-sm font-medium text-gray-700">Password</label>
                        <input
                            type={passwordVisible ? "text" : "password"}
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={handlePasswordChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        <button
                            type="button"
                            className="absolute right-3 top-3 text-gray-600"
                            onClick={togglePasswordVisibility}
                        >
                            {passwordVisible ? <FaEyeSlash /> : <FaEye />}
                        </button>
                    </div>

                    <div className="text-sm text-gray-700">
                        <p className="text-red-500">Password must contain:</p>
                        <div className="flex items-center gap-2">
                            <span className={`text-sm ${passwordCriteria.lowercase ? "text-green-600" : "text-gray-400"}`}>
                                ✔ One lowercase letter
                            </span>
                            <span className={`text-sm ${passwordCriteria.uppercase ? "text-green-600" : "text-gray-400"}`}>
                                ✔ One uppercase letter
                            </span>
                            <span className={`text-sm ${passwordCriteria.specialChar ? "text-green-600" : "text-gray-400"}`}>
                                ✔ One special character
                            </span>
                        </div>
                    </div>

                    <div className="flex flex-col">
                        <label htmlFor="confirmPassword" className="text-sm font-medium text-gray-700">Confirm Password</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            placeholder="Confirm Password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>

                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Sign Up
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Already have an account? {" "}
                    <button onClick={() => navigate("/login")} className="text-blue-500 underline">
                        Login
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
