import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash } from "react-icons/fa";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [passwordCriteria, setPasswordCriteria] = useState({
        lowercase: false,
        uppercase: false,
        specialChar: false,
    });

    const navigate = useNavigate();

    const togglePasswordVisibility = () => {
        setPasswordVisible(!passwordVisible);
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        setPassword(value);

        setPasswordCriteria({
            lowercase: /[a-z]/.test(value),
            uppercase: /[A-Z]/.test(value),
            specialChar: /[!@#$%^&*(),.?":{}|<>]/.test(value),
        });
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        if (!passwordCriteria.lowercase || !passwordCriteria.uppercase || !passwordCriteria.specialChar) {
            setError("Password does not meet the requirements.");
            return;
        }
        if (password !== confirmPassword) {
            setError("Passwords do not match.");
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            alert("Signup successful!");
            setTimeout(() => navigate("/login"), 2000);
        } catch (err) {
            setError(err.response?.data?.error || "Signup failed.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="w-full p-2 border rounded"
                        required
                    />
                    <div className="relative">
                        <input
                            type={passwordVisible ? "text" : "password"}
                            placeholder="Password"
                            value={password}
                            onChange={handlePasswordChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        <button
                            type="button"
                            className="absolute right-3 top-3 text-gray-600"
                            onClick={togglePasswordVisibility}
                        >
                            {passwordVisible ? <FaEyeSlash /> : <FaEye />}
                        </button>
                    </div>
                    {/* Password Criteria */}
                    <div className="text-sm text-gray-700">
                        <p className="text-red-500">Password must contain:</p>
                        <div className="flex items-center gap-2">
                            <div className={`w-4 h-4 border ${passwordCriteria.lowercase ? "bg-green-500" : "bg-gray-200"}`} />
                            <span>One lowercase letter</span>
                        </div>
                        <div className="flex items-center gap-2">
                            <div className={`w-4 h-4 border ${passwordCriteria.uppercase ? "bg-green-500" : "bg-gray-200"}`} />
                            <span>One uppercase letter</span>
                        </div>
                        <div className="flex items-center gap-2">
                            <div className={`w-4 h-4 border ${passwordCriteria.specialChar ? "bg-green-500" : "bg-gray-200"}`} />
                            <span>One special character</span>
                        </div>
                    </div>
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className="w-full p-2 border rounded"
                        required
                    />
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Sign Up
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Already have an account?{" "}
                    <button onClick={() => navigate("/login")} className="text-blue-500 underline">
                        Login
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
