import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [errors, setErrors] = useState({});
    const [authError, setAuthError] = useState("");
    const [passwordStrength, setPasswordStrength] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        validatePassword(password);
    }, [password]);

    const validateUsername = async (username) => {
        if (username.length < 8) {
            setErrors((prev) => ({ ...prev, username: "Username must be at least 8 characters long" }));
        } else {
            setErrors((prev) => {
                const { username, ...rest } = prev;
                return rest;
            });
        }
    };

    const validatePassword = (password) => {
        let missingCriteria = [];
        if (!/[a-z]/.test(password)) missingCriteria.push("one lowercase");
        if (!/[A-Z]/.test(password)) missingCriteria.push("one uppercase");
        if (!/[^a-zA-Z0-9]/.test(password)) missingCriteria.push("one special character");
        
        setPasswordStrength(missingCriteria.length === 0 ? "Strong" : "Weak");

        setErrors((prev) => ({
            ...prev,
            password: missingCriteria.length === 0 ? null : `Password must contain at least ${missingCriteria.join(", ")}`
        }));
    };

    const validateConfirmPassword = (confirmPassword) => {
        if (confirmPassword !== password) {
            setErrors((prev) => ({ ...prev, confirmPassword: "Passwords do not match" }));
        } else {
            setErrors((prev) => {
                const { confirmPassword, ...rest } = prev;
                return rest;
            });
        }
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        setAuthError("");
        
        if (!username || !password || !confirmPassword) {
            return setAuthError("All fields are required");
        }

        if (Object.keys(errors).length > 0) {
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            alert("Signup successful! Redirecting to login...");
            setTimeout(() => navigate("/login"), 2000);
        } catch (err) {
            setAuthError(err.response?.data?.error || "Signup failed. Try again.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {authError && <p className="text-red-500 text-sm mb-2 text-center">{authError}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <div>
                        <input
                            type="text"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => { setUsername(e.target.value); validateUsername(e.target.value); }}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {errors.username && <p className="text-red-500 text-xs">{errors.username}</p>}
                    </div>
                    <div>
                        <div className="relative">
                            <input
                                type={showPassword ? "text" : "password"}
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full p-2 border rounded"
                                required
                            />
                            <button
                                type="button"
                                className="absolute inset-y-0 right-2 flex items-center text-sm text-gray-600"
                                onClick={() => setShowPassword(!showPassword)}
                            >
                                {showPassword ? "Hide" : "Show"}
                            </button>
                        </div>
                        {errors.password && <p className="text-red-500 text-xs">{errors.password}</p>}
                        <p className={`text-xs ${passwordStrength === "Strong" ? "text-green-500" : "text-red-500"}`}>
                            Password Strength: {passwordStrength}
                        </p>
                    </div>
                    <div>
                        <input
                            type="password"
                            placeholder="Confirm Password"
                            value={confirmPassword}
                            onChange={(e) => { setConfirmPassword(e.target.value); validateConfirmPassword(e.target.value); }}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {errors.confirmPassword && <p className="text-red-500 text-xs">{errors.confirmPassword}</p>}
                    </div>
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Sign Up
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Already have an account?{" "}
                    <button onClick={() => navigate("/login")} className="text-blue-500 underline">
                        Login
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
