import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash } from "react-icons/fa";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [passwordStrength, setPasswordStrength] = useState("Weak");
    const [usernameError, setUsernameError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const [confirmPasswordError, setConfirmPasswordError] = useState("");
    
    const navigate = useNavigate();

    const togglePasswordVisibility = () => {
        setPasswordVisible(!passwordVisible);
    };

    const evaluatePasswordStrength = (value) => {
        let strength = "Weak";
        if (/[a-z]/.test(value) && /[A-Z]/.test(value) && /[!@#$%^&*(),.?":{}|<>]/.test(value)) {
            strength = value.length >= 8 ? "Strong" : "Medium";
        } else if (/[a-z]/.test(value) || /[A-Z]/.test(value)) {
            strength = "Medium";
        }
        setPasswordStrength(strength);
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        setPassword(value);
        evaluatePasswordStrength(value);
        
        if (!/[a-z]/.test(value) || !/[A-Z]/.test(value) || !/[!@#$%^&*(),.?":{}|<>]/.test(value)) {
            setPasswordError("Password must include lowercase, uppercase, and a special character.");
        } else {
            setPasswordError("");
        }
    };

    const handleUsernameChange = async (e) => {
        const value = e.target.value;
        setUsername(value);
        if (value.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
        } else {
            setUsernameError("");
            try {
                const response = await axios.post("http://localhost:5000/api/auth/check-username", { username: value });
                if (!response.data.isUnique) {
                    setUsernameError("Username is already taken.");
                }
            } catch (err) {
                setUsernameError("");
            }
        }
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
        setConfirmPasswordError(e.target.value !== password ? "Passwords do not match." : "");
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        if (usernameError || passwordError || confirmPasswordError) {
            setError("Please fix the errors before proceeding.");
            return;
        }
        try {
            await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            alert("Signup successful!");
            setTimeout(() => navigate("/login"), 2000);
        } catch (err) {
            setError(err.response?.data?.error || "Signup failed.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {error && <p className="text-red-500 text-sm text-center">{error}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <div className="flex flex-col">
                        <label htmlFor="username">Username</label>
                        <input type="text" id="username" value={username} onChange={handleUsernameChange} className="w-full p-2 border rounded" required />
                        {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    </div>

                    <div className="relative flex flex-col">
                        <label htmlFor="password">Password</label>
                        <input type={passwordVisible ? "text" : "password"} id="password" value={password} onChange={handlePasswordChange} className="w-full p-2 border rounded" required />
                        <button type="button" className="absolute right-3 top-3" onClick={togglePasswordVisibility}>{passwordVisible ? <FaEyeSlash /> : <FaEye />}</button>
                        {passwordError && <p className="text-red-500 text-sm">{passwordError}</p>}
                    </div>
                    
                    <div className="text-sm text-gray-700">Password Strength: <span className={`font-bold ${passwordStrength === "Strong" ? "text-green-600" : passwordStrength === "Medium" ? "text-yellow-500" : "text-red-500"}`}>{passwordStrength}</span></div>
                    
                    <div className="flex flex-col">
                        <label htmlFor="confirmPassword">Confirm Password</label>
                        <input type="password" id="confirmPassword" value={confirmPassword} onChange={handleConfirmPasswordChange} className="w-full p-2 border rounded" required />
                        {confirmPasswordError && <p className="text-red-500 text-sm">{confirmPasswordError}</p>}
                    </div>
                    
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">Sign Up</button>
                </form>
                <p className="mt-4 text-center">Already have an account? <button onClick={() => navigate("/login")} className="text-blue-500 underline">Login</button></p>
            </div>
        </div>
    );
}

export default Signup;
