import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [usernameError, setUsernameError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const [isUsernameValid, setIsUsernameValid] = useState(false);
    const [isPasswordValid, setIsPasswordValid] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Check if user is already logged in (based on token)
        if (localStorage.getItem("token")) {
            navigate("/landing");
        }
    }, [navigate]);

    const handleLogin = async (e) => {
        e.preventDefault();
        if (!isUsernameValid || !isPasswordValid) {
            setError("Please fill out the fields correctly.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/auth/login", { username, password });
            localStorage.setItem("token", response.data.token);
            localStorage.setItem("username", username); // Store username for greeting on landing page
            navigate("/landing");
        } catch (err) {
            if (err.response && err.response.data) {
                setError(err.response.data.error);
            } else {
                setError("An error occurred. Please try again.");
            }
        }
    };

    const handleUsernameChange = (e) => {
        const value = e.target.value;
        setUsername(value);
        setUsernameError("");
        if (value.length < 8) {
            setUsernameError("Username must be at least 8 characters.");
            setIsUsernameValid(false);
        } else {
            setIsUsernameValid(true);
        }
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        setPassword(value);
        setPasswordError("");
        if (value.length < 6) {
            setPasswordError("Password must be at least 6 characters.");
            setIsPasswordValid(false);
        } else {
            setIsPasswordValid(true);
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <form onSubmit={handleLogin} className="space-y-4">
                    {/* Username */}
                    <div>
                        <label htmlFor="username" className="block">Username</label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={handleUsernameChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {usernameError && <p className="text-red-500 text-sm">{usernameError}</p>}
                    </div>

                    {/* Password */}
                    <div>
                        <label htmlFor="password" className="block">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={handlePasswordChange}
                            className="w-full p-2 border rounded"
                            required
                        />
                        {passwordError && <p className="text-red-500 text-sm">{passwordError}</p>}
                    </div>

                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Login
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Don’t have an account?{" "}
                    <button onClick={() => navigate("/signup")} className="text-blue-500 underline">
                        Signup
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Login;
