import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaEye, FaEyeSlash } from "react-icons/fa";

function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [errors, setErrors] = useState({});
    const [authError, setAuthError] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const [passwordCriteria, setPasswordCriteria] = useState({
        lowercase: false,
        uppercase: false,
        specialChar: false
    });
    const navigate = useNavigate();

    useEffect(() => {
        validatePassword(password);
    }, [password]);

    const validatePassword = (password) => {
        const criteria = {
            lowercase: /[a-z]/.test(password),
            uppercase: /[A-Z]/.test(password),
            specialChar: /[^a-zA-Z0-9]/.test(password)
        };
        setPasswordCriteria(criteria);
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        setAuthError("");
        
        if (!username || !password || !confirmPassword) {
            return setAuthError("All fields are required");
        }

        if (password !== confirmPassword) {
            return setErrors((prev) => ({ ...prev, confirmPassword: "Passwords do not match" }));
        }

        if (!passwordCriteria.lowercase || !passwordCriteria.uppercase || !passwordCriteria.specialChar) {
            return setErrors((prev) => ({ ...prev, password: "Password does not meet criteria" }));
        }

        try {
            await axios.post("http://localhost:5000/api/auth/signup", { username, password });
            alert("Signup successful! Redirecting to login...");
            setTimeout(() => navigate("/login"), 2000);
        } catch (err) {
            setAuthError(err.response?.data?.error || "Signup failed. Try again.");
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="bg-white p-8 rounded-lg shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
                {authError && <p className="text-red-500 text-sm mb-2 text-center">{authError}</p>}
                <form onSubmit={handleSignup} className="space-y-4">
                    <div>
                        <input
                            type="text"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                    <div>
                        <div className="relative">
                            <input
                                type={showPassword ? "text" : "password"}
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full p-2 border rounded"
                                required
                            />
                            <button
                                type="button"
                                className="absolute inset-y-0 right-2 flex items-center text-sm text-gray-600"
                                onClick={() => setShowPassword(!showPassword)}
                            >
                                {showPassword ? <FaEyeSlash /> : <FaEye />}
                            </button>
                        </div>
                        <div className="mt-2 text-sm">
                            <div className="flex items-center gap-2">
                                <div className={`w-4 h-4 border ${passwordCriteria.lowercase ? "bg-green-500" : "bg-gray-300"}`}></div>
                                <span>One lowercase letter</span>
                            </div>
                            <div className="flex items-center gap-2">
                                <div className={`w-4 h-4 border ${passwordCriteria.uppercase ? "bg-green-500" : "bg-gray-300"}`}></div>
                                <span>One uppercase letter</span>
                            </div>
                            <div className="flex items-center gap-2">
                                <div className={`w-4 h-4 border ${passwordCriteria.specialChar ? "bg-green-500" : "bg-gray-300"}`}></div>
                                <span>One special character</span>
                            </div>
                        </div>
                    </div>
                    <div>
                        <input
                            type="password"
                            placeholder="Confirm Password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
                        Sign Up
                    </button>
                </form>
                <p className="mt-4 text-center">
                    Already have an account?{" "}
                    <button onClick={() => navigate("/login")} className="text-blue-500 underline">
                        Login
                    </button>
                </p>
            </div>
        </div>
    );
}

export default Signup;
